import React from 'react'

export default function About(props) {

    
    // const [myStyle, setMyStyle] = useState(
    //     {
    //         color : 'black',
    //         backgroundColor : 'white',
    //     }
    // )
    // const [sideBord, setSideBord] = useState(
    //     {
    //         border : ''
    //     }
        
    // )
    // const [btnText, setBtnText] = useState("Enable Dark mode")

    // const switchDark = ()=>{
    //     if(myStyle.color === 'black'){
    //         setMyStyle({
    //             color : 'white',
    //             backgroundColor : '#042743',
                
                
    //         })
    //         setSideBord({
    //             border : '1px solid pink'
    //         })
    //         setBtnText("Enable Light mode");
            
    //     }
    //     else{
    //         setMyStyle({
    //             color : 'black',
    //             backgroundColor : 'white'
    //         })
    //         setBtnText("Enable Dark mode");
    //         setSideBord({
    //             border : ''
    //         })
    //     }
    // }
        
    
  return (
        <div className="container" style={{color : props.mode === "light " ?"black" : "white" , backgroundColor : props.mode ==="light" ? "white" : "#042743" }}>
            <h1 className="my-2">About Us</h1>
            <div className="accordion" id="accordionExample" style={{border : props.mode === "dark " ?"pink" : "" }}>
                <div className="accordion-item" >
                    <h2 className="accordion-header">
                        <button className="accordion-button" type="button"  data-bs-toggle="collapse" data-bs-target="#collapseOne" aria-expanded="true" aria-controls="collapseOne">
                        Accordion Item #1
                        </button>
                    </h2>
                    <div id="collapseOne" className="accordion-collapse collapse show" data-bs-parent="#accordionExample" >
                        <div className="accordion-body">
                        <strong>This is the first item's accordion body.</strong> It is shown by default, until the collapse plugin adds the appropriate classNamees that we use to style each element. These classNamees control the overall appearance, as well as the showing and hiding via CSS transitions. You can modify any of this with custom CSS or overriding our default variables. It's also worth noting that just about any HTML can go within the <code>.accordion-body</code>, though the transition does limit overflow.
                        </div>
                    </div>
                </div>
                <div className="accordion-item" style={{border : props.mode === "dark " ?"pink" : "" }}>
                    <h2 className="accordion-header">
                        <button className="accordion-button collapsed" type="button"  data-bs-toggle="collapse" data-bs-target="#collapseTwo" aria-expanded="false" aria-controls="collapseTwo">
                        Accordion Item #2
                        </button>
                    </h2>
                    <div id="collapseTwo" className="accordion-collapse collapse" data-bs-parent="#accordionExample" >
                        <div className="accordion-body" >
                        <strong>This is the second item's accordion body.</strong> It is hidden by default, until the collapse plugin adds the appropriate classNamees that we use to style each element. These classNamees control the overall appearance, as well as the showing and hiding via CSS transitions. You can modify any of this with custom CSS or overriding our default variables. It's also worth noting that just about any HTML can go within the <code>.accordion-body</code>, though the transition does limit overflow.
                        </div>
                    </div>
                </div>
                <div className="accordion-item" style={{border : props.mode === "dark " ?"pink" : "" }}>
                    <h2 className="accordion-header">
                        <button className="accordion-button collapsed" type="button"  data-bs-toggle="collapse" data-bs-target="#collapseThree" aria-expanded="false" aria-controls="collapseThree">
                        Accordion Item #3
                        </button>
                    </h2>
                    <div id="collapseThree" className="accordion-collapse collapse" data-bs-parent="#accordionExample" >
                        <div className="accordion-body" >
                        <strong>This is the third item's accordion body.</strong> It is hidden by default, until the collapse plugin adds the appropriate classNamees that we use to style each element. These classNamees control the overall appearance, as well as the showing and hiding via CSS transitions. You can modify any of this with custom CSS or overriding our default variables. It's also worth noting that just about any HTML can go within the <code>.accordion-body</code>, though the transition does limit overflow.
                        </div>
                    </div>
                </div>
            </div>
            {/* <div className='container my-3'>
            <button type="button" onClick={switchDark} className="btn btn-primary ">{btnText}</button>
            </div> */}
        </div>
  )
}
