import React,{useState} from 'react'

export default function TextForm(props) {
    const [text,setText]  = useState("Write about your self--");
    // setText("new text "); // corrent way to change the state

    const handleUpClick= ()=>{
        // console.log("Uppercase was Click"+text);
        let newtext=text.toUpperCase();
        setText(newtext);
    }
        const handleOnChange= (event)=>{
            // console.log("On Change");
            setText(event.target.value);
    }
  return (
    <>
    <div className='container'>
        <label htmlFor="myBox" className="form-label"><h1>{props.heading }</h1></label>
        
        <div className="mb-3">
        <textarea className="form-control" value = {text} onChange={handleOnChange} id="myBox" rows="12"></textarea>
        </div>
        <button className="btn btn-primery" onClick={handleUpClick}>Convert to Uppercase</button>

    </div>
    <div className='container my-3'>
        <h1>Your text Summary </h1>
        <p>{text.split("").length} words and {text.length} characters</p>
    </div>
    </>
  )
}
