import React from 'react'

export default function TextForm(props) {
  return (
    <div>
        <label for="myBox" class="form-label"><h1>{props.heading}</h1></label>
        
        <div class="mb-3">
        <textarea className="form-control" id="myBox" rows="12"></textarea>
        </div>
        <button className="btn btn-primery">Convert to Uppercase</button>

    </div>
  )
}
