export default function TextForm(props) {
    const[text,setText]  = useState("Enter text here");
    // setText("Correct way "); // corrent way to change the state
  return (
    <div>
        <label htmlFor="myBox" className="form-label"><h1>{props.heading }</h1></label>
        
        <div className="mb-3">
        <textarea className="form-control" value = {text} id="myBox" rows="12"></textarea>
        </div>
        <button className="btn btn-primery">Convert to Uppercase</button>

    </div>
  )
}
